$(document).ready(function(){function e(){var e=new Date,t=6*e.getSeconds();return t}function t(){var e=new Date,t=6*e.getMinutes();return t}function n(){var e=new Date,t=e.getHours()%12/12*360+0;return t}var r=$('audio[data-key="second"');window.setInterval(function(){$(".second").css("transform","rotate("+(e()+360)+"deg)"),$(".minute").css("transform","rotate("+(t()+360)+"deg)"),$(".hour").css("transform","rotate("+(n()+360)+"deg)"),r[0].play()},1e3)});